def into_db(path_db, table_name, data):
    sql = """
    INSERT INTO {table_name} (id, word, translate, transcription, num_word_garibyan) VALUES (?, ?, ?, ?, ?)
    """.format(table_name=table_name)
    with sqlite3.connect(path_db) as db:
        cur = db.cursor()
        for data_i in data:
            data_ = list(data_i)
            data_[0:0] = [None]
            xxxxxx = tuple(data_)
            cur.execute(sql, xxxxxx)


def create_db(path_db, table_name):
    with sqlite3.connect(path_db) as db:
        sql = """
        CREATE TABLE IF NOT EXISTS {table_name} (id INTEGER PRIMARY KEY AUTOINCREMENT, word TEXT, translate TEXT,
         transcription TEXT, num_word_garibyan INTEGER)
        """.format(table_name=table_name)
        cur = db.cursor()
        cur.execute(sql)

path_db = "core_word_base.db"
create_db(path_db, "core_word_base")


def get_data(path_base, table_name, column_name):
    try:
        with sqlite3.connect(path_base) as con:
            cur = con.cursor()
            sql = """
            SELECT {column_name} FROM {table_name};
            """.format(column_name=column_name, table_name=table_name)
            res = cur.execute(sql)
            all = res.fetchall()
            if all:
                all = [i[0] for i in all]
            return all
    except sqlite3.DatabaseError as err:
        print("Ошибка ", err)


def get_field(path_db, table, word):
    sql = """
    SELECT num FROM {table} WHERE word='{word}'
    """.format(table=table, word=word)
    with sqlite3.connect(path_db) as db:
        cur = db.cursor()
        res = cur.execute(sql)
        return res.fetchall()


